'use strict';

var dbGeneral = require('../data/db-general.cjs');
var utils = require('../utils.cjs');

/**
 * Gera um apelido fictício aleatório.
 *
 * @returns {string} - Um apelido gerado aleatoriamente.
 * @example
 * console.log(apelido()); // Exemplo: "Falcão", "Astronauta", "Relíquia"
 */
function apelido() {
  return utils.getRandom(dbGeneral.nicknames);
}

exports.apelido = apelido;
