'use strict';

var dbGeneral = require('../data/db-general.cjs');
var utils = require('../utils.cjs');

/**
 * Gera cor da pele aleatória.
 * @type {string}
 * @returns {string} - Uma cor da pele aleatória.
 * @example
 * console.log(corPele()); // Exemplo: "Branca", "Preta", etc.
 */
function corPele() {
  return utils.getRandom(dbGeneral.coresPele);
}

exports.corPele = corPele;
