'use strict';

var dbGeneral = require('../data/db-general.cjs');
var utils = require('../utils.cjs');

/**
 * Gera uma orientação sexual aleatória.
 * @type {string}
 * @returns {string} - Uma orientação sexual aleatória.
 * @example
 * console.log(orientacaoSexual()); // Retorna qualquer orientação sexual, como: "Heterossexual", "Homossexual", etc.
 */
function orientacaoSexual() {
  return utils.getRandom(dbGeneral.orientacoesSexual);
}

exports.orientacaoSexual = orientacaoSexual;
