'use strict';

var dbGeneral = require('../data/db-general.cjs');
var utils = require('../utils.cjs');

/**
 * Gera uma profissão aleatória.
 * @type {string}
 * @returns {string} - Uma profissão aleatória.
 * @example
 * console.log(profissao()); // Retorna qualquer profissão, como "Engenheiro", "Médico", "Professor", etc.
 */
function profissao() {
  return utils.getRandom(dbGeneral.profissoes);
}

exports.profissao = profissao;
