'use strict';

var dbGeneral = require('../data/db-general.cjs');
var utils = require('../utils.cjs');

/**
 * Gera um provedor de email aleatório.
 * @type {string}
 * @returns {string} - Um provedor de email aleatório.
 * @example
 * console.log(provedorEmail()); // Retorna qualquer provedor de email, como: "@gmail.com", "@yahoo.com", etc.
 */
function provedorEmail() {
  return utils.getRandom(dbGeneral.provedoresEmail);
}

exports.provedorEmail = provedorEmail;
